public class A5Q7c {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1;i<=5;i++) {	
			for (int s = 4;s>=i;s--) {
				System.out.print("  ");
			}
			for (int n = 1;n<=i;n++) {
				System.out.print(i+" ");
			}
			System.out.println();
		}
	}
	
}


